import React from "react";
import { useParams } from "react-router-dom";
import Container from "react-bootstrap/esm/Container";
import { useSelector } from "react-redux";
import rootReducer from "../rootReducer";

function DetailsPage() {
    const {city} = useParams();
    const forecastData = useSelector(state => state);
    const forecastDataObj = forecastData.reducer.forecastData;

    return(
        <div className="details">
            <Container>
                <div className="details__base">
                    <p className="detail__city">Your city {city}</p>
                    <p className="detail__day">{forecastDataObj.day}</p>
                </div>
                {/* {/* <div className="details__icon">
                    <img src={forecastData.icon}/>
                </div> */}
                <div className="details__general">
                    <p>Minimum temperature: {forecastData.min}</p>
                    <p>Maximum temperature: {forecastData.max}</p>
                </div> 

                {/* grid (every 3 hour)*/}
                <div className="details__info">
                    {/* weather data for each new component (WeatherHours) with parametrs of the value*/}
                    
                </div>

                {/* info about wind */}
            </Container>
        </div>
    );  
}

export default DetailsPage;
