export const getShortNameOfDay = async (timestamp) => {
    return new Date(timestamp * 1000).toString().split(' ')[0];
}

export const getMinMaxTemp = (arrayOfHours) => {
    let minTemp = arrayOfHours[0].temp_c;
    let maxTemp = minTemp;

    for (const temp of arrayOfHours) {
        if (temp.temp_c < minTemp) {
            minTemp = temp.temp_c;
        }
        else if (temp.temp_c > maxTemp) {
            maxTemp = temp.temp_c;
        }
    }

    return [minTemp, maxTemp]
}
