import axios from "axios";
import React, { useEffect, useState } from "react";
import Cookies from 'js-cookie';
import {useCookies} from 'react-cookie';
import { DEFAULT_LOCATION } from "../config";
import sunny_icon from '../images/sunny_icon.png';
import rain_icon from '../images/rain_icon.png';
import clouds_icon from '../images/clouds_icon.png';
import snow_icon from '../images/snow_icon.png';
import thunderstorm_icon from '../images/thunderstorm_icon.png';
import drizzle_icon from '../images/drizzle_icon.png';
import mist_icon from '../images/mist_icon.png';
import sand_icon from '../images/sand_icon.png';
import ash_icon from '../images/ash_icon.png';
import tornado_icon from '../images/tornado_icon.png';
import squall_icon from '../images/squall_icon.png';
import Container from "react-bootstrap/esm/Container";
import WeatherOutput from './WeatherOutput';

function WeatherInput() {
    const city = localStorage.getItem('location') || DEFAULT_LOCATION;
    const [location, setLocation] = useState(city);
    // const [cookies, setCookie] = useCookies(['location']);
    const [forecast, setForecast] = useState([{
        day: 'current',
        temp: '',
        feels_like: '',
        descr: '',
    }]);

            // iconName: ''
        // });
    const [icon, setIcon] = useState('');
    const weatherData = {
        clear: sunny_icon,
        clouds: clouds_icon,
        // 'overcast clouds': overcast_clouds_icon,
        rain: rain_icon,
        snow: snow_icon,
        thunderstorm: thunderstorm_icon,
        drizzle: drizzle_icon,
        mist: mist_icon,
        smoke: mist_icon,
        haze: mist_icon,
        dust: mist_icon,
        fog: mist_icon,
        sand: sand_icon,
        ash: ash_icon,
        tornado: tornado_icon,
        squall: squall_icon
    }
    const dayOfWeek = ['current', 'monday', 'tuesday', 'wednesday', 'thursday', 'friday', 'saturday', 'sunday'];
    const dayOfWeekObj = {
        'Mon':'Monday',
        'Tue':'Tuesday',
        'Wed':'Wednesday',
        'Thu':'Thursday',
        'Fri':'Friday',
        'Sat':'Saturday',
        'Sun':'Sunday'
    }

    useEffect(() => {
        localStorage.setItem('location', location);
        
        forecast.map((val) => {
            Object.keys(weatherData).map((key, value) => {
                if (key == val.descr.toLowerCase()) {
                    setIcon(weatherData[key]);
                }
            });
        })

    }, [location, forecast]); 

    useEffect(() => {
        async function fetchData() {
            await getWeatherData();
        }
        fetchData();
        console.log(forecast);
        // dayOfWeek.forEach((value, index) => {
        //     <WeatherOutput forecastData={forecast[0]} icon={icon} day={value}/>
        // });

        // fetchData();
    }, []);

    function changeHandler(event) {
        setLocation(event.target.value);
    }

    async function getWeatherData() {
        const cityOutput = document.getElementsByClassName('weather__location')[0].getElementsByTagName('p')[0];

        try {
            cityOutput.innerHTML = `Your city: ${location}`; // || location
        
            const urlLocation = `https://api.openweathermap.org/geo/1.0/direct?q=${location}&limit=2&appid=adf38b48f7dadb4280bc1f2b2a841845`; //  ? cookies.location: location
            const response = await axios.get(urlLocation);
            const urlWeather = `https://api.openweathermap.org/data/2.5/weather?lat=${response.data[0].lat}&lon=${response.data[0].lon}&units=metric&appid=adf38b48f7dadb4280bc1f2b2a841845`;
            const urlForecast = `https://api.weatherapi.com/v1/forecast.json?key=7cca29f03a674182a96160005230108&q=${response.data[0].lat},${response.data[0].lon}&days=7&aqi=no&alerts=no`;
            const weatherResponse = await axios.get(urlWeather);
            const forecastResponse = await axios.get(urlForecast);
            const updateForecast = [{temp: weatherResponse.data.main.temp, feels_like: weatherResponse.data.main.feels_like, descr: weatherResponse.data.weather[0].main}]
            const forecastDays = forecastResponse.data.forecast.forecastday;
            
            for (let i = 0; i < forecastDays.length; i++) {
                Object.keys(dayOfWeekObj).forEach((key, index) => {
                    const date = new Date(forecastDays[i].date_epoch).toString().split(' ')[0];
                    
                    if (date == key.toString()) {
                        updateForecast = [{day:index, temp: weatherResponse.data.main.temp, feels_like: weatherResponse.data.main.feels_like, descr: weatherResponse.data.weather[0].main}]
                    }
                });
                // dayOfWeekObj.map((value, index) => {
                //     console.log(value)
                //     const date = new Date(forecastDays[i].date_epoch).toString().split(' ')[0];    
                //     console.log(value);

                // })
                
                // console.log(date);
            }
            
            setForecast(updateForecast);



            console.log(forecastResponse.data.forecast.forecastday[0])
            // console.log(forecastResponse)

        }
        catch (error) {
            cityOutput.innerHTML = 'Sorry, there is not such city.';
        }
    }

    async function searchLocation(event) {
        if (event.key == 'Enter') {
            await getWeatherData();
        }
    }

    return(
        <div className="weather">
            <Container>
                <div className="weather__location">
                    <input 
                    value={location}
                    onChange={changeHandler}
                    placeholder="Enter location"
                    onKeyDown = {searchLocation}
                    type="text"/>

                    <p>Your city: <span>{location}</span></p>
                </div>
                <div className="weather__forecast">
                    {new Array(forecast.length).fill().map((value, index) => <WeatherOutput forecastData={forecast[index].day} icon={icon} day={dayOfWeek[index]}/>)}
                </div>
                {/* <WeatherOutput forecastData={forecast} icon={icon} day={dayOfWeek[0]}/> */}
            </Container>
        </div>
    );
}

export default WeatherInput;
